# -*- coding: utf-8 -*-\n# @Time    : 2024/1/18 上午11:41\n# @Author  : sudoskys\nfrom fast_langdetect import detect, detect_multilingual, detect_language\n\ndef test_detect_multilingual():\n    """\n    Test the detect_multilingual function with various mixed language strings.\n    Demonstrates the function's ability to detect multiple languages in a single string.\n    """\n    # Test with a mixed language string using default parameters\n    result_default = detect_multilingual("Hello, world! 你好世界! Привет, мир!")\n    print("Default parameters result:", result_default)\n    \\(    # Test with a mixed language string using low_memory=True\n    result_low_memory = detect_multilingual("Hello, world! 你好世界! Привет, мир!", low_memory=True)\n    print("Low memory mode result:", result_low_memory)\n\ndef test_detect_single_language():\n    """\n    Test the detect function with single language strings.\n    Demonstrates the function's ability to detect a single language in a string.\n    """\n    # Test with English\n    print("Detect English:", detect("hello world"))\n    \\(    # Test with Chinese (Simplified)\n    print("Detect Chinese (Simplified):")\n    print(detect("你好世界"))\n    \\(    # Test with Russian\n    print("Detect Russian:")\n    print(detect("Привет, мир!"))\n    \\(    # Test with Japanese\n    print("Detect Japanese:")\n    print(detect("こんにちは世界"))\n    \\(    # Test with Korean\n    print("Detect Korean:")\n    print(detect("안녕하세요 세계"))\n    \\(    # Test with French\n    print("Detect French:")\n    print(detect("Bonjour le monde"))\n    \\(    # Test with German\n    print("Detect German:")\n    print(detect("Hallo Welt"))\n    \\(    # Test with Spanish\n    print("Detect Spanish:")\n    print(detect("Hola mundo"))\n    \\(    # Test with Chinese (Traditional)\n    print("Detect Chinese (Traditional):")\n    print(detect("這些機構主辦的課程，多以基本電腦使用為主，例如文書處理、中文輸入、互聯網應用等"))\n\ndef test_detect_language_single():\n    """\n    Test the detect_language function with single language strings.\n    Demonstrates the function's ability to detect a single language in a string and return the language code.\n    """\n    # Test with English\n    print("Detect language English:", detect_language("hello world"))\n    \\(    # Test with Chinese (Simplified)\n    print("Detect language Chinese (Simplified):")\n    print(detect_language("你好世界"))\n    \\(    # Test with Russian\n    print("Detect language Russian:")\n    print(detect_language("Привет, мир!"))\n    \\(    # Test with Japanese\n    print("Detect language Japanese:")\n    print(detect_language("こんにちは世界"))\n    \\(    # Test with Korean\n    print("Detect language Korean:")\n    print(detect_language("안녕하세요 세계"))\n    \\(    # Test with French\n    print("Detect language French:")\n    print(detect_language("Bonjour le monde"))\n    \\(    # Test with German\n    print("Detect language German:")\n    print(detect_language("Hallo Welt"))\n    \\(    # Test with Spanish\n    print("Detect language Spanish:")\n    print(detect_language("Hola mundo"))\n    \\(    # Test with Chinese (Traditional)\n    print("Detect language Chinese (Traditional):")\n    print(detect_language("這些機構主辦的課程，多以基本電腦使用為主，例如文書處理、中文輸入、互聯網應用等"))\n