# -*- coding: utf-8 -*-\n# @Time    : 2024/1/18 上午11:41\n# @Author  : sudoskys\nfrom fast_langdetect import detect, detect_multilingual, detect_language\n\n# 测试多语言检测功能，低内存模式\nprint(detect_multilingual("Hello, world!你好世界!Привет, мир!", low_memory=True))\n# 测试多语言检测功能，正常内存模式\nprint(detect_multilingual("Hello, world!你好世界!Привет, мир!", low_memory=False))\nprint(detect("hello world"))\n# 测试单语言检测功能\nprint(detect_language("Привет, мир!"))  # 俄语\nprint(detect_language("你好世界"))      # 简体中文\nprint(detect_language("こんにちは世界")) # 日语\nprint(detect_language("안녕하세요 세계")) # 韩语\nprint(detect_language("Bonjour le monde")) # 法语\nprint(detect_language("Hallo Welt"))     # 德语\nprint(detect_language("Hola mundo"))     # 西班牙语\nprint(detect_language("這些機構主辦的課程，多以基本電腦使用為主，例如文書處理、中文輸入、互聯網應用等")) # 繁体中文