# -*- coding: utf-8 -*-\n# @Time    : 2024/1/18 上午11:41\n# @Author  : sudoskys\nfrom fast_langdetect import detect, detect_multilingual, detect_language\n\n# 测试多语言检测功能，低内存模式\nprint(detect_multilingual("Hello, world!你好世界!Привет, мир!", low_memory=True))\n# [{'lang': 'ja', 'score': 0.32009604573249817}, {'lang': 'uk', 'score': 0.27781224250793457}, {'lang': 'zh', 'score': 0.17542070150375366}, {'lang': 'sr', 'score': 0.08751443773508072}, {'lang': 'bg', 'score': 0.05222449079155922}]\n# 测试多语言检测功能，正常内存模式\nprint(detect_multilingual("Hello, world!你好世界!Привет, мир!", low_memory=False))\n# [{'lang': 'ja', 'score': 0.32009604573249817}, {'lang': 'uk', 'score': 0.27781224250793457}, {'lang': 'zh', 'score': 0.17542070150375366}, {'lang': 'sr', 'score': 0.08751443773508072}, {'lang': 'bg', 'score': 0.05222449079155922}]\n# 测试单语言检测功能\nprint(detect("hello world"))  # 预期输出: {'lang': 'en'}\n# 测试单语言检测功能，多种语言\nprint(detect_language("Привет, мир!"))  # 俄语，预期输出: 'RU'\nprint(detect_language("你好世界"))      # 简体中文，预期输出: 'ZH'\nprint(detect_language("こんにちは世界")) # 日语，预期输出: 'JA'\nprint(detect_language("안녕하세요 세계")) # 韩语，预期输出: 'KO'\nprint(detect_language("Bonjour le monde")) # 法语，预期输出: 'FR'\nprint(detect_language("Hallo Welt"))     # 德语，预期输出: 'DE'\nprint(detect_language("Hola mundo"))     # 西班牙语，预期输出: 'ES'\nprint(detect_language("這些機構主辦的課程，多以基本電腦使用為主，例如文書處理、中文輸入、互聯網應用等")) # 繁体中文，预期输出: 'ZH'\