# -*- coding: utf-8 -*-\n# @Time    : 2024/1/18 上午11:41\n# @Author  : sudoskys\nfrom fast_langdetect import detect, detect_multilingual, detect_language\n\n# 测试多语言检测功能，低内存模式\nprint(detect_multilingual("Hello, world!你好世界!Привет, мир!", low_memory=True))\n# 测试多语言检测功能，正常内存模式\nprint(detect_multilingual("Hello, world!你好世界!Привет, мир!", low_memory=False))\n# 测试单语言检测功能，英文\nprint(detect("hello world"))  # 预期输出: {'lang': 'en'}\n# 测试单语言检测功能，俄语\nprint(detect("Привет, мир!"))  # 预期输出: {'lang': 'ru'}\n# 测试单语言检测功能，简体中文\nprint(detect("你好世界"))      # 预期输出: {'lang': 'zh'}\n# 测试单语言检测功能，日语\nprint(detect("こんにちは世界")) # 预期输出: {'lang': 'ja'}\n# 测试单语言检测功能，韩语\nprint(detect("안녕하세요 세계")) # 预期输出: {'lang': 'ko'}\n# 测试单语言检测功能，法语\nprint(detect("Bonjour le monde")) # 预期输出: {'lang': 'fr'}\n# 测试单语言检测功能，德语\nprint(detect("Hallo Welt"))     # 预期输出: {'lang': 'de'}\n# 测试单语言检测功能，西班牙语\nprint(detect("Hola mundo"))     # 预期输出: {'lang': 'es'}\n# 测试单语言检测功能，繁体中文\nprint(detect_language("這些機構主辦的課程，多以基本電腦使用為主，例如文書處理、中文輸入、互聯網應用等")) # 预期输出: 'ZH'\