# -*- coding: utf-8 -*-\n# @Time    : 2024/1/18 上午11:41\n# @Author  : sudoskys\nfrom fast_langdetect import detect, detect_multilingual, detect_language\n\n# 测试多语言检测功能，正常内存模式\nprint(detect_multilingual("Hello, world!你好世界!Привет, мир!", low_memory=False))\n# 测试单语言检测功能\nprint(detect("hello world"))  # 预期输出: {'lang': 'en'}\n# 测试单语言检测功能，多种语言\nprint(detect("Привет, мир!"))  # 俄语，预期输出: 'RU'\nprint(detect("你好世界"))      # 简体中文，预期输出: 'ZH'\nprint(detect("こんにちは世界")) # 日语，预期输出: 'JA'\nprint(detect("안녕하세요 세계")) # 韩语，预期输出: 'KO'\nprint(detect("Bonjour le monde")) # 法语，预期输出: 'FR'\nprint(detect("Hallo Welt"))     # 德语，预期输出: 'DE'\nprint(detect("Hola mundo"))     # 西班牙语，预期输出: 'ES'\nprint(detect("這些機構主辦的課程，多以基本電腦使用為主，例如文書處理、中文輸入、互聯網應用等")) # 繁体中文，预期输出: 'ZH'\