# -*- coding: utf-8 -*-\n# @Time    : 2024/1/18 上午11:41\n# @Author  : sudoskys\nfrom fast_langdetect import detect, detect_multilingual, detect_language\n\n# Test detect_multilingual with a mix of languages\Tests the ability to detect multiple languages in a single string\nprint(detect_multilingual("Hello, world! \\\\u4f60\\u597d\\u4e16\\u754c! \\\\u041f\\u0440\\u0438\\u0432\\u0435\\u0442, \\\\u043c\\u0438\\u0440! \\\\u3053\\u308a\\u308a\\u306f\\u4e16\\u754c!"))\nprint(detect_multilingual("Hello, world! \\\\u4f60\\u597d\\u4e16\\u754c! \\\\u041f\\u0440\\u0438\\u0432\\u0435\\u0442, \\\\u043c\\u0438\\u0440! \\\\u3053\\u308a\\u308a\\u306f\\u4e16\\u754c!", low_memory=True))\n\n# Test detect with various languages\Tests the detection of individual languages\nprint(detect("hello world"))  # English\nprint(detect("\\\\u4f60\\u597d\\u4e16\\u754c"))  # Chinese (Simplified)\nprint(detect("\\\\u041f\\u0440\\u0438\\u0432\\u0435\\u0442, \\\\u043c\\u0438\\u0440!"))  # Russian\nprint(detect("\\\\u3053\\u308a\\u308a\\u306f\\u4e16\\u754c"))  # Japanese\nprint(detect("\\\\u0041\\u004e\\u00eb\\u0085\\u00b8\\u00ed\\u0082\\u00ac\\u00ec\\u0088\\u0098\\u00ec\\u008a\\u00b9 \\\\u00ec\\u0088\\u0098\\u00ec\\u008a\\u00b9"))  # Korean\nprint(detect("Bonjour le monde"))  # French\nprint(detect("Hallo Welt"))  # German\nprint(detect("Hola mundo"))  # Spanish\nprint(detect("\\\\u90a3\\u9ebb\\u6a21\\u7d44\\u4e3b\\u5355\\u7684\\u8bfe\\u7a0b\\u3002\\u591a\\u4ee5\\u57fa\\u672c\\u8a9e\\u8a00\\u4f7f\\u7528\\u70ba\\u4e3b\\u3002\\u4f8b\\u5982\\u6587\\u6863\\u8655\\u7406\\u3001\\u4e2d\\u6587\\u8f38\\u5165\\u3001\\u4e92\\u8fde\\u7db2\\u5e02\\u5712\\u7b49"))  # Chinese (Traditional)\n\n# Test detect_language with various languages\Tests the detection of individual languages and returns the language code\nprint(detect_language("hello world"))  # English\nprint(detect_language("\\\\u4f60\\u597d\\u4e16\\u754c"))  # Chinese (Simplified)\nprint(detect_language("\\\\u041f\\u0440\\u0438\\u0432\\u0435\\u0442, \\\\u043c\\u0438\\u0440!"))  # Russian\nprint(detect_language("\\\\u3053\\u308a\\u308a\\u306f\\u4e16\\u754c"))  # Japanese\nprint(detect_language("\\\\u0041\\u004e\\u00eb\\u0085\\u00b8\\u00ed\\u0082\\u00ac\\u00ec\\u0088\\u0098\\u00ec\\u008a\\u00b9 \\\\u00ec\\u0088\\u0098\\u00ec\\u008a\\u00b9"))  # Korean\nprint(detect_language("Bonjour le monde"))  # French\nprint(detect_language("Hallo Welt"))  # German\nprint(detect_language("Hola mundo"))  # Spanish\nprint(detect_language("\\\\u90a3\\u9ebb\\u6a21\\u7d44\\u4e3b\\u5355\\u7684\\u8bfe\\u7a0b\\u3002\\u591a\\u4ee5\\u57fa\\u672c\\u8a9e\\u8a00\\u4f7f\\u7528\\u70ba\\u4e3b\\u3002\\u4f8b\\u5982\\u6587\\u6863\\u8655\\u7406\\u3001\\u4e2d\\u6587\\u8f38\\u5165\\u3001\\u4e92\\u8fde\\u7db2\\u5e02\\u5712\\u7b49"))  # Chinese (Traditional)\n