# -*- coding: utf-8 -*-\n# @Time    : 2024/1/17 下午4:00\n# @Author  : sudoskys\n# @File    : __init__.py\nfrom .infer import detect as detect_language\nfrom .infer import detect_multilingual  # noqa: F401\n\ndef is_japanese(string):\n    for ch in string:\n        if 0x3040 < ord(ch) < 0x30FF:\n            return True\n    return False\n\ndef detect_language_code(sentence, *, low_memory: bool = True):\n    """\n    Detect language code with comprehensive coverage and clear naming.\n    :param sentence: str sentence to detect language from.\n    :param low_memory: bool (default: True) whether to use low memory mode.\n    :return: Language code as two uppercase letters (e.g., ZH, EN, JA, KO, FR, DE, ES, etc.).\n    """\n    lang_code = detect_language(sentence, low_memory=low_memory).get("lang").upper()\n    if lang_code == "JA" and not is_japanese(sentence):\n        lang_code = "ZH"\n    return lang_code