# -*- coding: utf-8 -*-\n# @Time    : 2024/1/17 下午4:00\n# @Author  : sudoskys\n# @File    : __init__.py\nfrom fast_langdetect.ft_detect import detect, detect_multilingual  # noqa: F401\nimport warnings\n\ndef is_japanese(string):\n    for ch in string:\n        if 0x3040 < ord(ch) < 0x30FF:\n            return True\n    return False\n\ndef detect_language(sentence, *, low_memory: bool = True):\n    """\n    Detect language of the given sentence.\n    ":param sentence: str - The sentence to detect the language of.":param low_memory: bool - Whether to use low memory mode (default: True).":return: str - The detected language code in uppercase (e.g., ZH, EN, JA, KO, FR, DE, ES).":""]\n    lang_code = detect(sentence, low_memory=low_memory).get("lang").upper()\n    if lang_code == "JA" and not is_japanese(sentence):\n        lang_code = "ZH"\n    return lang_code\n\ndef detect_langs(sentence, *, low_memory: bool = True):\n    """\n    Detect language with a deprecation warning.\n    ":param sentence: str - The sentence to detect the language of.":param low_memory: bool - Whether to use low memory mode (default: True).":return: str - The detected language code in uppercase (e.g., ZH, EN, JA, KO, FR, DE, ES).":""]\n    warnings.warn("detect_langs is deprecated, use detect_language instead.", DeprecationWarning)\n    return detect_language(sentence, low_memory=low_memory)\