# -*- coding: utf-8 -*-\n# @Time    : 2024/1/17 下午4:00\n# @Author  : sudoskys\n# @File    : __init__.py\nfrom .infer import detect, detect_multilingual  # noqa: F401\n\ndef is_japanese_text(text):\n    """Check if the text contains Japanese characters."""\n    for ch in text:\n        if 0x3040 < ord(ch) < 0x30FF:\n            return True\n    return False\n\ndef detect_languages(sentence, *, low_memory: bool = True):\n    """Detect the language of the given sentence.\n    ":param sentence: The sentence to detect the language of.":param low_memory: Whether to use low memory mode.":return: Language code in uppercase (e.g., ZH, EN, JA, KO, FR, DE, ES)."""\n    lang_code = detect(sentence, low_memory=low_memory).get("lang").upper()\n    if lang_code == "JA" and not is_japanese_text(sentence):\n        lang_code = "ZH"\n    return lang_code