# -*- coding: utf-8 -*-\n# @Time    : 2024/1/17 下午5:28\n# @Author  : sudoskys\n# @File    : test_detect.py\n# @Software: PyCharm\n\ndef test_muti_detect():\n    """\n    Test the detect_multilingual function with a simple multilingual input.\n    """\n    from fast_langdetect.ft_detect import detect_multilingual    \\n    result = detect_multilingual("Hello, 你好, Привет!", low_memory=True)\n    assert result[0].get("lang") == "en", "ft_detect error"\n    assert result[1].get("lang") == "zh", "ft_detect error"\n    assert result[2].get("lang") == "ru", "ft_detect error"\n\ndef test_detect():\n    """\n    Test the detect function with single languages.\n    """\n    from fast_langdetect import detect    \\n    assert detect("hello world")["lang"] == "en", "ft_detect error"\n    assert detect("你好世界")["lang"] == "zh", "ft_detect error"\n    assert detect("こんにちは世界")["lang"] == "ja", "ft_detect error"\n    assert detect("안녕하세요 세계")["lang"] == "ko", "ft_detect error"\n    assert detect("Bonjour le monde")["lang"] == "fr", "ft_detect error"\n    assert detect("Hallo Welt")["lang"] == "de", "ft_detect error"\n    assert detect("Hola mundo")["lang"] == "es", "ft_detect error"\n    assert detect("Ces institutions organisent des cours principalement sur l'utilisation de base de l'informatique, par exemple le traitement de texte, la saisie chinoise, les applications internet, etc.")["lang"] == "fr", "ft_detect error"\n    assert detect("这些机构主办的课程，多以基本电脑使用为主，例如文书处理、中文输入、互联网应用等")["lang"] == "zh", "ft_detect error"\n\ndef test_detect_totally():\n    """\n    Test the detect_language function with single languages.\n    """\n    from fast_langdetect import detect_language    \\n    assert detect_language("hello world") == "en", "ft_detect error"\n    assert detect_language("你好世界") == "zh", "ft_detect error"\n    assert detect_language("こんにちは世界") == "ja", "ft_detect error"\n    assert detect_language("안녕하세요 세계") == "ko", "ft_detect error"\n    assert detect_language("Bonjour le monde") == "fr", "ft_detect error"\n    assert detect_language("Hallo Welt") == "de", "ft_detect error"\n    assert detect_language("Hola mundo") == "es", "ft_detect error"\n    assert detect_language("Ces institutions organisent des cours principalement sur l'utilisation de base de l'informatique, par exemple le traitement de texte, la saisie chinoise, les applications internet, etc.") == "fr", "ft_detect error"\n    assert detect_language("这些机构主办的课程，多以基本电脑使用为主，例如文书处理、中文输入、互联网应用等") == "zh", "ft_detect error"\