# -*- coding: utf-8 -*-\n# @Time    : 2024/1/17 下午5:28\n# @Author  : sudoskys\n# @File    : test_detect.py\n# @Software: PyCharm\n\ndef test_multilingual_detection():\n    """Test the multilingual detection functionality."""\n    from fast_langdetect import detect_multilingual    \\n    result = detect_multilingual("hello world", low_memory=True)\n    assert result[0]["lang"] == "en", "Multilingual detection error"\n\ndef test_language_detection():\n    """Test the language detection for various languages."""\n    from fast_langdetect import detect_languages    \\)n    \\)n    result = detect_languages("hello world")\n    assert result[0]["lang"] == "en", "Language detection error for English"\n    \\)n    result = detect_languages("你好世界")\n    assert result[0]["lang"] == "zh", "Language detection error for Chinese"\n    \\)n    result = detect_languages("こんにちは世界")\n    assert result[0]["lang"] == "ja", "Language detection error for Japanese"\n    \\)n    result = detect_languages("안녕하세요 세계")\n    assert result[0]["lang"] == "ko", "Language detection error for Korean"\n    \\)n    result = detect_languages("Bonjour le monde")\n    assert result[0]["lang"] == "fr", "Language detection error for French"\n    \\)n    result = detect_languages("Hallo Welt")\n    assert result[0]["lang"] == "de", "Language detection error for German"\n    \\)n    result = detect_languages("Hola mundo")\n    assert result[0]["lang"] == "es", "Language detection error for Spanish"\