# -*- coding: utf-8 -*-\n# @Time    : 2024/1/17 \:52:28\n# @Author  : sudoskys\n# @File    : test_detect.py\n# @Software: PyCharm\n#\n# This file contains test cases for the fast_langdetect library to demonstrate its capabilities in detecting multiple languages in a single text.\n#\n# Example usage:\n# - Detecting multiple languages in a single string.\n# - Detecting the primary language of a string.\n# - Detecting the language of a string and returning the language code in uppercase.\n#\n# The tests below verify the accuracy of the language detection functions.\n#\n# Multilingual detection example:\n# The function `detect_multilingual` is used to identify multiple languages present in a single text.\n# It returns a list of dictionaries, each containing the detected language and its confidence score.\n#\n# Single language detection example:\n# The function `detect` is used to identify the primary language of a given text.\n# It returns a dictionary with the detected language and its confidence score.\n#\n# Language code detection example:\n# The function `detect_language` is used to identify the primary language of a given text.\n# It returns the language code in uppercase.\n#\n\ndef test_multilingual_detection():\n    from fast_langdetect.ft_detect import detect_multilingual    \\n    result = detect_multilingual("Hello, world!你好世界!Привет, мир!", low_memory=False)\n    # Expected output: [{'lang': 'ja', 'score': ...}, {'lang': 'uk', 'score': ...}, {'lang': 'zh', 'score': ...}, ...]\n    assert any(item.get("lang") == "ja" for item in result), "Japanese not detected"\n    assert any(item.get("lang") == "uk" for item in result), "Ukrainian not detected"\n    assert any(item.get("lang") == "zh" for item in result), "Chinese not detected"\n    print("Multilingual detection passed")\n\ndef test_single_language_detection():\n    from fast_langdetect import detect    \\n    assert detect("hello world")["lang"] == "en", "Test failed: English detection"\n    assert detect("你好世界")["lang"] == "zh", "Test failed: Chinese detection"\n    assert detect("こんにちは世界")["lang"] == "ja", "Test failed: Japanese detection"\n    assert detect("안녕하세요 세계")["lang"] == "ko", "Test failed: Korean detection"\n    assert detect("Bonjour le monde")["lang"] == "fr", "Test failed: French detection"\n    assert detect("Hallo Welt")["lang"] == "de", "Test failed: German detection"\n    assert detect("Hola mundo")["lang"] == "es", "Test failed: Spanish detection"\n    print("Single language detection passed")\n\ndef test_language_code_detection():\n    from fast_langdetect import detect_language    \\n    assert detect_language("hello world") == "EN", "Test failed: English language code detection"\n    assert detect_language("你好世界") == "ZH", "Test failed: Chinese language code detection"\n    assert detect_language("こんにちは世界") == "JA", "Test failed: Japanese language code detection"\n    assert detect_language("안녕하세요 세계") == "KO", "Test failed: Korean language code detection"\n    assert detect_language("Bonjour le monde") == "FR", "Test failed: French language code detection"\n    assert detect_language("Hallo Welt") == "DE", "Test failed: German language code detection"\n    assert detect_language("Hola mundo") == "ES", "Test failed: Spanish language code detection"\n    assert detect_language(\