# -*- coding: utf-8 -*-\n# @Time    : 2024/1/17 下午5:28\n# @Author  : sudoskys\n# @File    : test_detect.py\n# @Software: PyCharm\n\ndef test_multilingual():\n    """Test the multilingual detection functionality."""\n    from fast_langdetect.ft_detect import detect_multilingual    \\n    result = detect_multilingual("hello world", low_memory=True)\n    assert result[0].get("lang") == "en", "Language detection error for multilingual"\n\ndef test_language_detection():\n    """Test the language detection for various languages."""\n    from fast_langdetect import detect_language    \\)n    assert detect_language("hello world").get("lang") == "en", "Language detection error for English"\n    assert detect_language("你好世界").get("lang") == "zh", "Language detection error for Chinese"\n    assert detect_language("こんにちは世界").get("lang") == "ja", "Language detection error for Japanese"\n    assert detect_language("안녕하세요 세계").get("lang") == "ko", "Language detection error for Korean"\n    assert detect_language("Bonjour le monde").get("lang") == "fr", "Language detection error for French"\n    assert detect_language("Hallo Welt").get("lang") == "de", "Language detection error for German"\n    assert detect_language("Hola mundo").get("lang") == "es", "Language detection error for Spanish"\n    assert detect_language("這些機構主辦的課程，多以基本電腦使用為主，例如文書處理、中文輸入、互聯網應用等").get("lang") == "zh", "Language detection error for Traditional Chinese"\n\ndef test_detect_totally():\n    """Test the language detection with various inputs."""\n    from fast_langdetect import detect_language    \\)n    assert detect_language("hello world").get("lang") == "en", "Language detection error for English"\n    assert detect_language("你好世界").get("lang") == "zh", "Language detection error for Chinese"\