# -*- coding: utf-8 -*-\n# @Time    : 2024/1/17 下午5:28\n# @Author  : sudoskys\n# @File    : test_detect.py\n# @Software: PyCharm\n\ndef test_muti_detect():\n    """Test the multilingual detection functionality."""\n    from fast_langdetect.ft_detect import detect_multilingual    \\n    result = detect_multilingual("hello world", low_memory=True)\n    assert result[0]["lang"] == "en", "ft_detect error"\n\ndef test_detect():\n    """Test the language detection for various languages."""\n    from fast_langdetect import detect_language    \\)n    assert detect_language("hello world")["lang"] == "en", "ft_detect error"\n    assert detect_language("你好世界")["lang"] == "zh", "ft_detect error"\n    assert detect_language("こんにちは世界")["lang"] == "ja", "ft_detect error"\n    assert detect_language("안녕하세요 세계")["lang"] == "ko", "ft_detect error"\n    assert detect_language("Bonjour le monde")["lang"] == "fr", "ft_detect error"\n    assert detect_language("Hallo Welt")["lang"] == "de", "ft_detect error"\n    assert detect_language("Hola mundo")["lang"] == "es", "ft_detect error"\n    assert detect_language("這些機構主辦的課程，多以基本電腦使用為主，例如文書處理、中文輸入、互聯網應用等")["lang"] == "zh", "ft_detect error"\